</div>');
INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (22, 'https://jobs.techstars.com/companies/green-eye-technology-2/jobs/51986932-financial-controller-temporary-position#content', 'Financial Controller - Temporary position', 'https://jobs.techstars.com/companies/green-eye-technology-2?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkFjY291bnRpbmclMjAlMjYlMjBGaW5hbmNlIl19#content', 'https://cdn.getro.com/companies/4444fd41-3e1c-53ec-96ff-ccfe992e0eaa-1750209206', 'Green Eye Technology', 'Accounting & Finance', 1748995200, '<div>
 <div>
  <div>
