INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (51, 'https://jobs.techstars.com/companies/and-global/jobs/49376911-financial-reporting-manager#content', 'Financial reporting manager', 'https://jobs.techstars.com/companies/and-global?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkFjY291bnRpbmclMjAlMjYlMjBGaW5hbmNlIl19#content', 'https://cdn.getro.com/companies/4e5d8981-366d-58a3-b962-0e21601e63b7-1747532068', 'AND Global', 'Accounting & Finance', 1744934400, '<p><span>The Role:</span></p>
<p>The Financial reporting manager is responsible for managing the production of financial reports for the company, including corporate accounting and financial reports regarding business performance, and presenting to company directors, shareholders, auditors and regulators. Reviews reports to analyze trends as well as help management to make important financial decisions that will affect the company’s budget and finances. Also, monitors company financial statements and reports to verify their accuracy and to find ways to limit or reduce costs.<span> </span></p>
<p><span>Duties:</span></p>
