INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (64, 'https://jobs.techstars.com/companies/outreach/jobs/52800031-director-escalation-and-supportability#content', 'Director, Escalation and Supportability', 'https://jobs.techstars.com/companies/outreach?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkN1c3RvbWVyJTIwU2VydmljZSJdfQ%3D%3D#content', 'https://cdn.getro.com/companies/22032d7f-dc7e-5e26-8ce0-e2109d3817b7-1748914032', 'Outreach', 'Customer Service', 1750204800, '<section>
