INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (85, 'https://jobs.techstars.com/companies/and-global/jobs/52263394-support-engineer#content', 'Support engineer', 'https://jobs.techstars.com/companies/and-global?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkN1c3RvbWVyJTIwU2VydmljZSJdfQ%3D%3D#content', 'https://cdn.getro.com/companies/4e5d8981-366d-58a3-b962-0e21601e63b7-1747532068', 'AND Global', 'Customer Service', 1749427200, '<p><span>General Description</span></p>
