INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (184, 'https://jobs.techstars.com/companies/newsela/jobs/52217131-multimedia-producer#content', 'Multimedia Producer', 'https://jobs.techstars.com/companies/newsela?filter=eyJqb2JfZnVuY3Rpb25zIjpbIk1hcmtldGluZyUyMCUyNiUyMENvbW11bmljYXRpb25zIl19#content', 'https://cdn.getro.com/companies/751af8b4-32a7-55bc-9fad-8bfbcbbf4237-1748914246', 'Newsela', 'Marketing & Communications', 1749254400, '<h3><span><strong>About the role:</strong></span></h3>
<ul>
 <li><span>Newsela and Generation Genius are launching a bold new animated video series, and we’re looking for a versatile, hands-on <strong>Multimedia Producer</strong> to join us at the ground floor. </span></li>
 <li><span>As our <strong>first full-time multimedia team member</strong>, you’ll play a key role in shaping the creative and production processes that will define this new studio effort.</span></li>
