INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (185, 'https://jobs.techstars.com/companies/padsplit/jobs/52158292-marketing-automation-specialist-philippines#content', 'Marketing Automation Specialist (Philippines)', 'https://jobs.techstars.com/companies/padsplit?filter=eyJqb2JfZnVuY3Rpb25zIjpbIk1hcmtldGluZyUyMCUyNiUyMENvbW11bmljYXRpb25zIl19#content', 'https://cdn.getro.com/companies/dcc60547-1cc2-5c47-a5de-4f4652e0d483-1748914902', 'PadSplit', 'Marketing & Communications', 1749168000, '<section>
