INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (191, 'https://jobs.techstars.com/companies/tomocredit/jobs/51823778-summer-marketing-internship-graphic-design-video-production#content', 'Summer Marketing Internship - Graphic Design & Video Production', 'https://jobs.techstars.com/companies/tomocredit?filter=eyJqb2JfZnVuY3Rpb25zIjpbIk1hcmtldGluZyUyMCUyNiUyMENvbW11bmljYXRpb25zIl19#content', 'https://cdn.getro.com/companies/2ab9d21c-c5ab-5df2-85ab-792fcc3da56e-1749001413', 'TomoCredit', 'Marketing & Communications', 1748649600, '<div id="job-description">
