  <li><span><span><span><span><span><span>Insurance</span></span></span></span></span></span><span><span><span><span><span><span>: Comprehensive medical, dental, and vision benefits.</span></span></span></span></span></span></li>
  <li><span><span><span><span><span><span>Flexible Vacation Policy</span></span></span></span></span></span><span><span><span><span><span><span>: We trust you to manage your time wisely to prevent burnout.</span></span></span></span></span></span></li>
