INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (204, 'https://jobs.techstars.com/companies/propellane/jobs/51514611-stage-alternance-en-marketing-sales-leads-propellane-startup-cleantech#content', 'Stage / Alternance en Marketing & Sales leads - Propellane (startup cleantech)', 'https://jobs.techstars.com/companies/propellane?filter=eyJqb2JfZnVuY3Rpb25zIjpbIk1hcmtldGluZyUyMCUyNiUyMENvbW11bmljYXRpb25zIl19#content', 'https://cdn.getro.com/companies/fa6a2b46-dc5f-53b3-8106-a49a786d752d-1750176808', 'Propellane', 'Marketing & Communications', 1748044800, '<div>
