INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (210, 'https://jobs.techstars.com/companies/abby-by-gogotech-2/jobs/51705024-director-of-creative-services-project-management#content', 'Director of Creative Services (Project Management)', 'https://jobs.techstars.com/companies/abby-by-gogotech-2?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkFkbWluaXN0cmF0aW9uIl19#content', 'https://cdn.getro.com/companies/34ec752a-20d9-5208-88ff-4ddc7bc09b8d-1747963112', 'ABBY by GOGOTECH', 'Administration', 1748476800, '<section>
