INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (213, 'https://jobs.techstars.com/companies/partyslate/jobs/51431055-customer-success-manager#content', 'Customer Success Manager', 'https://jobs.techstars.com/companies/partyslate?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkFkbWluaXN0cmF0aW9uIl19#content', 'https://cdn.getro.com/companies/59ddb6a2-f58d-5394-94fc-1140f0e90957-1748914281', 'PartySlate', 'Administration', 1747958400, '<section>
