INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (244, 'https://jobs.techstars.com/companies/newsela/jobs/52832127-contractor-ai-automation-lead#content', 'Contractor: AI Automation Lead', 'https://jobs.techstars.com/companies/newsela?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkRhdGElMjBTY2llbmNlIl19#content', 'https://cdn.getro.com/companies/751af8b4-32a7-55bc-9fad-8bfbcbbf4237-1748914246', 'Newsela', 'Data Science', 1750204800, '<p>Seeking to hire a Contractor based out of Argentina or Mexico for AI Automation Lead services</p>
<p><strong>About: </strong></p>
<p>Newsela is seeking a highly driven and technically fluent <strong>AI Automation Lead</strong> based in LATAM to help us streamline and scale our Sales and Customer Success operations using AI. In this hybrid role, you’ll act as both <strong>prompt engineer</strong> and <strong>AI product owner</strong> — identifying high-impact automation opportunities, building them using Gemini + Google Workspace and other AI tools, and deploying solutions that save our GTM teams time and money.</p>
<p></p>
<p><strong>Scope of Services:</strong></p>
<ul>
