INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (255, 'https://jobs.techstars.com/companies/moksa-ai/jobs/52398214-moksa-ai-computer-vision-engineer-deep-learning#content', 'mokSa.ai - Computer Vision Engineer - Deep Learning', 'https://jobs.techstars.com/companies/moksa-ai?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkRhdGElMjBTY2llbmNlIl19#content', 'https://cdn.getro.com/companies/7903636b-b380-52d2-a74d-e8be287bd8dc-1748396481', 'mokSa AI', 'Data Science', 1749600000, '<div>
 <strong>Job Description<br><br></strong>We are seeking a talented Computer Vision Engineer with strong expertise in microservice deployment architecture to join our team.
 <br>
 <br>
 In this role, you will be responsible for developing and deploying computer vision models to analyze retail surveillance footage for use cases such as theft detection, employee efficiency monitoring, and store traffic analysis.
 <br>
 <br><strong>Responsibilities<br><br></strong>You will work on designing and implementing scalable, cloud-based microservices to deliver real-time and post-event analytics to improve retail Responsibilities : 
 <br>
 <br>
 <ul>
  <li>Develop computer vision models : Build, train, and optimize deep learning models to analyze surveillance footage for detecting theft, monitoring employee productivity, tracking store busy hours, and other relevant use cases.</li>
  <li>Microservice architecture : Design and deploy scalable microservice-based solutions that allow seamless integration of computer vision models into cloud or on-premise environments.</li>
  <li>Data processing pipelines : Develop data pipelines to process real-time and batch video data streams,<br><br></li>
 </ul>ensuring efficient extraction, transformation, and loading (ETL) of video data.
 <br>
 <br>
 <ul>
  <li>Integrate with existing systems : Collaborate with backend and frontend engineers to integrate computer vision services with existing retail systems such as POS, inventory management, and employee scheduling.</li>
  <li>Performance optimization : Fine-tune models for high accuracy and real-time inference on edge devices or cloud infrastructure, optimizing for latency, power consumption, and resource constraints.</li>
