INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (266, 'https://jobs.techstars.com/companies/voids/jobs/51020304-senior-data-scientist-demand-forecasting-f-m-d#content', 'Senior Data Scientist - Demand Forecasting (f/m/d)', 'https://jobs.techstars.com/companies/voids?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkRhdGElMjBTY2llbmNlIl19#content', 'https://cdn.getro.com/companies/06ed8f1c-4829-549e-947d-6cc7185b9838-1748136885', 'VOIDS', 'Data Science', 1747267200, '<div>
