INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (267, 'https://jobs.techstars.com/companies/adsgency-ai-2/jobs/51019379-member-of-technical-staff-ai-agent#content', 'Member of Technical Staff, AI Agent', 'https://jobs.techstars.com/companies/adsgency-ai-2?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkRhdGElMjBTY2llbmNlIl19#content', 'https://cdn.getro.com/companies/e3cd0834-6cb4-5e7d-9d80-10ca35df486b-1748741801', 'AdsGency AI', 'Data Science', 1747267200, '<div>
