INSERT INTO job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (411, 'https://jobs.techstars.com/companies/kepler-communications-2/jobs/52493275-hardware-quality-engineering-specialist#content', 'Hardware Quality Engineering Specialist', 'https://jobs.techstars.com/companies/kepler-communications-2?filter=eyJqb2JfZnVuY3Rpb25zIjpbIk90aGVyJTIwRW5naW5lZXJpbmciXX0%3D#content', 'https://cdn.getro.com/companies/c24d89fd-21b5-5f32-a9b7-c11e5826173d-1748310180', 'Kepler Communications', 'Other Engineering', 1749686400, '<section>
