INSERT INTO `parser-db`.job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (571, 'https://jobs.techstars.com/companies/testlio/jobs/51091886-freelance-software-tester-uber-driver-canada#content', 'Freelance Software Tester/Uber Driver (Canada)', 'https://jobs.techstars.com/companies/testlio?filter=eyJqb2JfZnVuY3Rpb25zIjpbIlF1YWxpdHklMjBBc3N1cmFuY2UiXX0%3D#content', 'https://cdn.getro.com/companies/17d9185b-1399-5bb7-b219-cf74aa4f6902-1748914598', 'Testlio', 'Quality Assurance', 1747353600, '<div>
