INSERT INTO `parser-db`.job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (585, 'https://jobs.techstars.com/companies/nabu-2-732afd6d-81df-4c02-a274-80c383eb1295/jobs/48688438-declarant-en-douane-customs-compliance-quality-engineer#content', 'Déclarant en douane (Customs Compliance & Quality Engineer)', 'https://jobs.techstars.com/companies/nabu-2-732afd6d-81df-4c02-a274-80c383eb1295?filter=eyJqb2JfZnVuY3Rpb25zIjpbIlF1YWxpdHklMjBBc3N1cmFuY2UiXX0%3D#content', 'https://cdn.getro.com/companies/19a58685-785b-5610-b227-5aa61540e289-1750262682', 'Nabu', 'Quality Assurance', 1744243200, '<div id="the-position-section">
 <div>
