INSERT INTO `parser-db`.job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (597, 'https://jobs.techstars.com/companies/tomocredit/jobs/44741547-business-analyst-strategic-finance#content', 'Business Analyst, Strategic Finance', 'https://jobs.techstars.com/companies/tomocredit?filter=eyJqb2JfZnVuY3Rpb25zIjpbIkFjY291bnRpbmclMjAlMjYlMjBGaW5hbmNlIl19#content', 'https://cdn.getro.com/companies/2ab9d21c-c5ab-5df2-85ab-792fcc3da56e-1749001413', 'TomoCredit', 'Accounting & Finance', 1736812800, '<div id="job-description">
