INSERT INTO `parser-db`.job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (616, 'https://jobs.techstars.com/companies/padsplit/jobs/52827722-staff-engineer-frontend-development#content', 'Staff Engineer, Frontend Development', 'https://jobs.techstars.com/companies/padsplit#content', 'https://cdn.getro.com/companies/dcc60547-1cc2-5c47-a5de-4f4652e0d483-1748914902', 'PadSplit', 'Product', 1750204800, '<section>
