INSERT INTO `parser-db`.job_postings (id, job_page_url, position_name, organization_url, logo_url, organization_title, labor_function, posted_date_unix, description_html) VALUES (630, 'https://jobs.techstars.com/companies/coachcorner-2/jobs/52763914-sports-instructor-basketball#content', 'Sports Instructor - Basketball', 'https://jobs.techstars.com/companies/coachcorner-2#content', 'https://cdn.getro.com/companies/f9374990-3cca-51f1-ba04-e0e1b8b4aeb8-1748881088', 'CoachCorner', 'Quality Assurance', 1750118400, '<div>
 <p>South Shore YMCA<br>
   Staten Island, NY</p>
 <div id="benefits">
